// Parse ticker from Discord message
const content = ($json.content || '').trim();

// Ignore messages from bots
if ($json.author?.bot) {
  return [];
}

// Don't process if message doesn't look like a ticker request
if (!content || content.length < 1) {
  return [];
}

// Accept forms like: SPY, $AAPL, analyze MSFT, !ticker SPY, check QQQ
const tickerMatch = content.toUpperCase().match(/(?:^|\s)(?:\$|!ticker\s+|analyze\s+|check\s+)?([A-Z]{1,5})(?:\s|$)/);

if (!tickerMatch) {
  return [];
}

const ticker = tickerMatch[1];

// Determine trade type from message content
let trade_type = 'day'; // default
if (content.toLowerCase().includes('swing')) {
  trade_type = 'swing';
} else if (content.toLowerCase().includes('both')) {
  trade_type = 'both';
}

return [{
  ticker: ticker,
  trade_type: trade_type,
  channel_id: $json.channel_id,
  message_id: $json.id,
  author: $json.author?.username || 'Unknown'
}];

// Format the analysis response for Discord
const data = $json;
const ticker = data.ticker || 'UNKNOWN';
const plans = data.plans || [];

if (plans.length === 0) {
  return [{
    response: `**${ticker}** - No trade setups found\n\n` +
              `The 3-Tier Multi-Timeframe analysis did not find any high-confidence trade setups. ` +
              `This typically means:\n` +
              `‚Ä¢ Trends don't align across timeframes\n` +
              `‚Ä¢ Market is ranging/choppy\n` +
              `‚Ä¢ No clear entry trigger\n\n` +
              `*This is good - the bot avoids low-probability trades!*`,
    channel_id: $('Parse Ticker & Trade Type').item.json.channel_id,
    message_id: $('Parse Ticker & Trade Type').item.json.message_id
  }];
}

// Format the highest confidence plan
const plan = plans[0]; // Plans are sorted by confidence

const directionEmoji = plan.direction === 'long' ? 'üìà' : 'üìâ';
const confidenceStars = '‚≠ê'.repeat(Math.min(plan.confidence, 5));

// Format edges
const edgesText = plan.edges_applied && plan.edges_applied.length > 0
  ? plan.edges_applied.map(e => `‚úì ${e}`).join('\n')
  : 'None';

// Build response
const response = `${directionEmoji} **${ticker}** - ${plan.trade_type.toUpperCase()} TRADE SETUP\n\n` +
  `**Direction:** ${plan.direction.toUpperCase()}\n` +
  `**Confidence:** ${plan.confidence}/5 ${confidenceStars}\n\n` +
  `**Entry:** $${plan.entry}\n` +
  `**Stop:** $${plan.stop}\n` +
  `**Target 1:** $${plan.target2 || plan.target} (1R)\n` +
  `**Target 2:** $${plan.target} (2R)\n` +
  `**Risk/Reward:** ${plan.risk_reward}R\n\n` +
  `**Edges Applied:**\n${edgesText}\n\n` +
  `**Rationale:**\n${plan.rationale}\n\n` +
  `**Risk Notes:**\n${plan.risk_notes}\n\n` +
  `*Analysis Timestamp: ${new Date(data.analysis_timestamp).toLocaleString('en-US', { timeZone: 'America/Chicago' })} CT*`;

// If there are multiple plans, mention it
let additionalPlans = '';
if (plans.length > 1) {
  additionalPlans = `\n\n*Note: ${plans.length - 1} additional ${plans.length - 1 === 1 ? 'plan' : 'plans'} available with lower confidence*`;
}

return [{
  response: response + additionalPlans,
  channel_id: $('Parse Ticker & Trade Type').item.json.channel_id,
  message_id: $('Parse Ticker & Trade Type').item.json.message_id
}];
