// FIXED: Account for double body nesting
const data = $input.item.json.body.body || $input.item.json.body;
const content = data.content || '';

// Ignore bot messages
if (data.author?.bot) {
  return [];
}

// Extract ticker - support $SPY or !ticker SPY formats
let ticker = null;

if (content.startsWith('$')) {
  // Format: $SPY
  ticker = content.substring(1).toUpperCase().trim();
} else if (content.toLowerCase().startsWith('!ticker ')) {
  // Format: !ticker SPY
  ticker = content.substring(8).toUpperCase().trim();
}

// If no ticker found, stop processing
if (!ticker) {
  return [];
}

// Remove any extra text after ticker (just get the symbol)
ticker = ticker.split(' ')[0];

// Validate ticker is 1-5 letters
if (!/^[A-Z]{1,5}$/.test(ticker)) {
  return [];
}

// ALWAYS return 'both' - no need to parse trade type
return [{
  json: {
    ticker: ticker,
    trade_type: 'both',
    channel_id: data.channel_id,
    message_id: data.id,
    author: data.author?.username || 'Unknown'
  }
}];

// Format the analysis response for Discord
const data = $input.item.json;
const ticker = data.ticker || 'UNKNOWN';
const plans = data.plans || [];

if (plans.length === 0) {
  return {
    response: `**${ticker}** - No trade setups found\n\n` +
              `The 3-Tier Multi-Timeframe analysis did not find any high-confidence trade setups. ` +
              `This typically means:\n` +
              `â€¢ Trends don't align across timeframes\n` +
              `â€¢ Market is ranging/choppy\n` +
              `â€¢ No clear entry trigger\n\n` +
              `*This is good - the bot avoids low-probability trades!*`
  };
}

// Helper function to format a single plan
function formatPlan(plan, ticker) {
  const directionEmoji = plan.direction === 'long' ? 'ðŸ“ˆ' : 'ðŸ“‰';
  const confidenceStars = 'â­'.repeat(Math.min(plan.confidence, 5));

  const edgesText = plan.edges_applied && plan.edges_applied.length > 0
    ? plan.edges_applied.map(e => `âœ“ ${e}`).join('\n')
    : 'None';

  return `${directionEmoji} **${ticker}** - ${plan.trade_type.toUpperCase()} TRADE\n\n` +
    `**Direction:** ${plan.direction.toUpperCase()}\n` +
    `**Confidence:** ${plan.confidence}/5 ${confidenceStars}\n\n` +
    `**Entry:** $${plan.entry}\n` +
    `**Stop:** $${plan.stop}\n` +
    `**Target 1:** $${plan.target2 || plan.target} (1R)\n` +
    `**Target 2:** $${plan.target} (2R)\n` +
    `**Risk/Reward:** ${plan.risk_reward}R\n\n` +
    `**Edges:**\n${edgesText}\n\n` +
    `**Rationale:**\n${plan.rationale}\n\n` +
    `**Risk Notes:** ${plan.risk_notes}`;
}

// Format all plans, separated by dividers
const formattedPlans = plans.map(plan => formatPlan(plan, ticker)).join('\n\n' + 'â”€'.repeat(50) + '\n\n');

const response = formattedPlans + '\n\n' +
  `*Analysis: ${new Date(data.analysis_timestamp).toLocaleString('en-US', { timeZone: 'America/Chicago' })} CT*`;

return {
  response: response
};
